package practiceprograms;

public class Constructor {
	
	Constructor(){ // this is an exxample of default constructor
		System.out.println("This is a constructor");
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Constructor is a special type of method used to initialize the object. It is invoked during run time.
		// There are two types: 1. Default Constructor and 2. Parameterized Constructor
		Constructor obj = new Constructor();
	}

}
